script:
(
    function(){
       const userImage = root_element.querySelector('.user-image');
       const userName = root_element.querySelector('.user-name');
       const userRole = root_element.querySelector('.user-role');

       // Update the user Name.
        userName.innerHTML = frappe.session.user_fullname;

        // Update the avatar.
        userImage.innerHTML = frappe.avatar();

        // update the user role.
        userRole.innerHTML = 'System Manager';

    }
)();

style:
.hms-dashboard-user-card{
    display: flex;

    justify-content: flex-start;
    gap: 10px;
    align-items: flex-end;
    padding: 5rem;
    border-radius: 5px; 
    text-align: center;
}

.user-image .avatar{
    height: 10rem;
    width: 10rem;
}
